fold.atomic.css:
/* ATOMIC.CSS (served virtually by sw.js) */
:root{
  --bg-0:#020409;--bg-1:#050912;--bg-2:#071022;
  --panel:rgba(255,255,255,.045);--panel2:rgba(255,255,255,.06);
  --border:rgba(255,255,255,.14);--border-soft:rgba(255,255,255,.09);
  --fg-0:#eafff8;--fg-1:rgba(234,255,248,.78);--fg-2:rgba(234,255,248,.56);
  --accent:#16f2aa;--accent2:#61e7ff;--accent-soft:rgba(22,242,170,.14);
  --hazard:#ffb300;--hazard-soft:rgba(255,179,0,.14);
  --danger:#ff6b6b;--success:#00d9a6;--warning:#ffb300;
}
/* Minimal atomic utilities (expand later into your full ATOMIC.CSS v5) */
.asx-flex{display:flex}.asx-col{flex-direction:column}.asx-row{flex-direction:row}
.asx-center{align-items:center;justify-content:center}
.asx-card{background:var(--panel);border:1px solid var(--border);border-radius:12px}
.asx-btn{background:var(--panel);border:1px solid var(--border);border-radius:8px;color:var(--fg-1);padding:8px 12px;cursor:pointer}
.asx-btn:hover{background:var(--panel2);border-color:var(--border-soft)}
